import React, {Component} from 'react';
import './ForgotPassword.css';
import axios from 'axios';

class ResetPassword extends Component{

    constructor(props) {
        super(props);
        this.state = {
            newPassword: '',
            confirmPassword:'',
            token:''
        }

    }
    changeData = (event) => {
        event.preventDefault();
        this.setState({
            [event.target.name]: event.target.value
        })

    };

    render(){
        return(
            <div className="background" onSubmit={this.postData}>
                <div className="frame">
                    <header>Forgot Password ?</header>
                    <header className="id">Please enter your email account:</header>
                    <form className="forgot_form" id="email_form">

                        <div className="field">
                            <span className="fa fa-envelope"/>
                            <input type="password" name='newPassword' placeholder="New Password"  onChange={this.changeData} required/>
                        </div>
                        <br/>
                        <br/>
                        <button className="submit_btn">  <span className="fa fa-paper-plane"/> Send</button>

                    </form>
                </div>
            </div>

        );
    }





}

export default ResetPassword;
