import React, {Component} from 'react';
import './Login.css';
import axios from 'axios'

class Login extends Component {

    constructor(props) {
        super(props);
        this.state = {
            email: '',
            password: ''
        }
    }

   postData = (event) =>{
        event.preventDefault();
        const email = this.state.email;
        const password = this.state.password;
        const data = {email, password};

        axios.post('http://localhost:8080/users/logIn', data)
            .then(res => {
                if(res.data === false){
                    console.log(res.data + ": logging failed");
                    alert("Email or password incorrect")
                }else {
                    localStorage.setItem('userId' ,email);
                    console.log("logged in successfully : " + localStorage.getItem('userId'));
                    document.getElementById("log_in_form").reset();   //Reset the form after submit
                    // eslint-disable-next-line no-restricted-globals
                   /* location.reload();
                    this.openHomePage();*/
                }
            })
    };

/*    openHomePage(){
        window.open(sessionStorage.getItem('lastPage'), '_self');
    }*/

    changeData = (event) => {
        event.preventDefault();
        console.log(event.target.value);
        this.setState({
            [event.target.name]: event.target.value
        })

    };

    render() {

        return (
            <div className="bg-img" onSubmit={this.postData}>
                <div className="content">
                    <header>Login Form</header>
                    <form action="#">
                        <div className="field">
                            <span className="fa fa-user"/>
                            <input type="text" name='email'  placeholder="Email" onChange={this.changeData} required/>
                        </div>
                        <div className="field space">
                            <span className="fa fa-lock"/>
                            <input type="password" name='password' className="pass-key"  placeholder="Password"
                                   onChange={this.changeData} required/>
                                <span className="show">SHOW</span>
                        </div>
                        <div className="pass">
                            <a href="#">Forgot Password?</a>
                        </div>
                        <div className="field">
                            <input type="submit" value="LOGIN"/>
                        </div>

                    </form>
                </div>

            </div>
        );
    }

}

export default Login;
