import React, {Component} from 'react';
import {MenuItems} from "./MenuItems";
import '../Navbar/Navbar.css'
import './Button';

class Navbar extends Component {

    state={clicked:false,
    isLogged:false
    }

    componentDidMount() {
        if(localStorage.getItem('token') === null){
            this.setState({'isLogged':false})
        }else{
            this.setState({'isLogged':true})
        }
    }

    handleClick =() =>{
        this.setState({clicked:!this.state.clicked});
    };

    checkLogged = ()=>{
        return(localStorage.getItem('userId') != null)
    }

    navigate = () =>{
        if(this.state.isLogged){
            localStorage.removeItem('token');
            return '/';
        }
        return '/Login'
    }

    render() {
        return (
            <nav className="NavbarItems">
              {/*  <meta name="viewport" content="width=device-width , initial-scale=1.0">*/}
                <h1 className="NavbarLogo">React project<i className="fab fa-react"> </i></h1>

                <div className="menuIcon" onClick={this.handleClick}>

                    <i className={this.state.clicked ? 'fas fa-times' : 'fas fa-bars'}/>
                </div>

                <ul className={this.state.clicked? 'nav-menu active' : 'nav-menu'}>
                    {MenuItems.map((item, index) => {
                        return (
                            <li key={index}>
                                <a className={item.cName} href={item.url}> <i className={ 'fa fa-'+item.icon}> </i>
                                    {item.title}
                                </a>
                            </li>
                        )
                    })}
                </ul>
                <a href={this.navigate()} className="button"><i className= "fa fa-user"> </i>{this.state.isLogged? 'Logout':'Login'}</a>
            </nav>
        )

    }
}

export default Navbar;
