import React, {Component} from 'react';
import './Sign_up.css';

class Sign_up extends Component {

    constructor(props) {
        super(props);
        this.state = {
            firstName: '',
            lastName:'',
            email: '',
            phoneNumber: '',
            password1: '',
            password2:'',
        };
        this.inputFocus =React.createRef();
    }

    changeData = (event) => {
        event.preventDefault();
        this.setState({
            [event.target.name]: event.target.value
        })
    };

    render() {
        return (
            <div className="container-fluid">
                <h1 id="Sign up">Sign up</h1>
                <br/>
                <form className="sign_up_form" id="sign_up_form" >

                    <input type="text" id="name" name="name" placeholder="First Name"  required/>
                    <br/>

                    <br/>
                    <input type="text" id="last_name" name="last_name" placeholder="Last Name" required/>
                    <br/>

                    <br/>
                    <input type="email" id="email" name="email" placeholder="Email Address"
                           required/>
                    <br/>
                    <br/>
                    <input type="password" id="password1" name="password1" placeholder="Password" minLength="8"
                            required/>

                    <br/>
                    <br/>
                    <input ref={this.inputFocus} type="password" id="password2" name="password2" placeholder="Confirm password" minLength="8"
                           onChange={this.changeData} required/>
                    <br/>
                    <br/>
                    <input type="text" id="phone" name="phoneNumber" placeholder="Phone"  minLength="10"
                           required/>
                    <br/>
                    <br/>
                    <button  className="submit_button" type="submit" id="submit">Submit</button>

                </form>

            </div>
        );
    }



}

export default Sign_up;
