import React, {Component} from 'react';
import {MenuItems} from "./MenuItems";
import '../Navbar/Navbar.css'
import './Button';

class Navbar extends Component {
    state={clicked:false}
    handleClick =() =>{
        this.setState({clicked:!this.state.clicked});
    };

    render() {
        return (
            <nav className="NavbarItems">
                <h1 className="NavbarLogo">React project<i className="fab fa-react"> </i></h1>

                <div className="menuIcon" onClick={this.handleClick}>

                    <i className={this.state.clicked ? 'fas fa-times' : 'fas fa-bars'}/>
                </div>

                <ul className={this.state.clicked? 'nav-menu active' : 'nav-menu'}>
                    {MenuItems.map((item, index) => {
                        return (
                            <li key={index}>
                                <a className={item.cName} href={item.url}> <i className={ 'fa fa-'+item.icon}> </i>
                                    {item.title}
                                </a>
                            </li>
                        )
                    })}
                </ul>
                <a href="/Login" className="button"><i className= "fa fa-user"> </i>Login</a>
            {/*    <button className="btn" ref='/Login'>  <i className= "fa fa-user"> </i> Login </button>*/}
            </nav>
        )

    }
}

export default Navbar;
