import React, {Component} from 'react';
import './Sign_up.css';
import axios from 'axios'

class Sign_up extends Component {

    constructor(props) {
        super(props);
        this.state = {
            firstName: '',
            lastName:'',
            email: '',
            phoneNumber: '',
            password: '',
            confirmPassword:'',
            errorMessage:''
        };
        this.inputFocus =React.createRef();
    }

    postData = (event) => {
        event.preventDefault();
        const password = this.state.password;
        if(password !== this.state.confirmPassword ){
            alert('The password and confirmation password dont match');
            this.inputFocus.current.focus();
            return;
        }
        const firstName = this.state.firstName;
        const lastName = this.state.lastName;
        const email = this.state.email;
        const phoneNumber = this.state.phoneNumber;
        const data = {firstName,lastName , email, phoneNumber, password};
        console.log(data)
        axios.post('http://localhost:8080/users/register', data)
            .then(res => {
                console.log(res.data);
                alert("Added successfully !");
                document.getElementById("sign_up_form").reset();   //Reset the form after submit
                //window.open('/Log_in', '_self');
            }).catch(error => {
            console.log(error);
            alert(error.response.data.message)
        })
    };

    checkValidation = (e)=>{
        this.setState({'confirmPassword':e.target.value});
        if(this.state.password === this.state.confirmPassword){
            this.setState({'errorMessage':'Confirm Password Should Be Match With Password'})
        }

    }

    changeData = (event) => {
        event.preventDefault();
        this.setState({
            [event.target.name]: event.target.value
        })
    };



    render() {
        return (
            <div className="container-fluid" onSubmit={this.postData} >
                <h1 className="form_title" id="Sign up">Register</h1>
                <br/>
                <form className="sign_up_form" id="sign_up_form" >

                    <input className="input" type="text" id="name" name="firstName" placeholder="First Name"onChange={this.changeData}  required/>
                    <br/>
                    <br/>
                    <br/>
                    <input className="input" type="text" id="last_name" name="lastName" placeholder="Last Name"onChange={this.changeData} required/>
                    <br/>
                    <br/>
                    <br/>
                    <input className="input" type="email" id="email" name="email" placeholder="Email Address"
                           onChange={this.changeData} required/>
                    <br/>
                    <br/>
                    <br/>
                    <input className="input" type="password" id="password1" name="password1" placeholder="Password" minLength="8"
                           onChange={this.changeData} required/>
                    <br/>
                    <br/>
                    <br/>
                    <input className="input" ref={this.inputFocus} type="password" id="password2" name="password2" placeholder="Confirm password" minLength="8"
                           onChange={this.changeData}  onClick={ this.checkValidation} required />
                    <span className="symbol-input">
                          <i className="fa fa-lock" aria-hidden="true"> </i>
                    </span>

                    <br/>
                    <br/>
                    <br/>
                    <input className="input" type="text" id="phone" name="phoneNumber" placeholder="Phone Number" minLength="10"
                           onChange={this.changeData} required/>

                    <br/>
                    <br/>
                    <div className="error-message">{this.state.errorMessage}</div>

                    <br/>
                    <br/>
                    <button  className="submit_button" type="submit" id="submit">Sign Up</button>

                </form>

            </div>

        );
    }

}

export default Sign_up;
